import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import Default from './Default';
import Product from '../components/Product';
import { listProducts } from './actions/productActions';
import { ProductConsumer } from '../context';
import { storeProducts } from '../data';

export default function SearchScreen(props) {
  const { name = 'all' } = useParams();
  const newarr = storeProducts.find(element => element.includes(name));
  const filteredProducts = storeProducts.filter((product) => {
    if (
      product.id.toLowerCase().includes(search) ||
      product.title.toLowerCase().includes(search) ||
      product.company.toLowerCase().includes(search)
    ) {
      return product;
    }
  });
  return (
    <div>
        <div>{newarr}</div>
      <div className="row">
        {newarr.length==0 ? (
          <Default></Default>
        ): (
          <div>{newarr.length} Results</div>
        )}
      </div>
      <div className="row top">
        <div className="col-1">
          <h3>Department</h3>
          <ul>
            <li>Categoery 1</li>
          </ul>
        </div>
        <div className="col-3">
          {newarr.length==0 ? (
            <Default></Default>
          ) : (
            <>
                <div className="py-5">
                    <div className="container">
                            <div className="row"> 
                            <ProductConsumer>
                                {

                                    value => {
                                        return (
                                            value.filteredProducts.map( product => { return <Product key={product.id} product={product}/>;} )
                                            );
                                    }
                                }
                            </ProductConsumer>
                            </div>
                    </div>
                </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
