import React, { Component } from 'react'

// for css
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {ProductConsumer} from '../context';
import PropTypes from 'prop-types';
import { storeProducts } from '../data'; 


//2.03.00 te kaldım
export default class Product extends Component {
    render() {
        for(let i=0;i<storeProducts.length;i++)
        { 
          if(storeProducts[i].id == this.props.product.id)
          {
            const {id, title, img, price, inCart} = storeProducts[i];
          }
        }

        return (
            <ProductWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3" > 
              <div className = "card"> 
              <ProductConsumer>
                { value => (


              <div className = "img-container p-5" onClick = {() => value.handleDetail(id)}>
              <Link to= "/details">
                  <img src = {img} alt="product image" className="card-img-top"/>
              </Link>
              <button className="card-btn" disabled = {inCart?true:false} onClick={ () => {
                value.addToCart(id);
                value.openModal(id);
            }}>
              {inCart?(
              <p className="text-capitalize mb-0" disabled>
                {""}
                in Cart
              </p>
              ):(
                <i className="fas fa-cart-plus" />
              )}
              </button>
            </div>



                )}

              </ProductConsumer>
              {/* card footer */}
              <div className="card-footer d-flex justify-content-between">
              <p className="align-self-center mb-0">
              {title}</p>
              <h5 className="text-blue font-italic mb-0">
                  <span className="mr-1">$</span>
                  {price}
              </h5>


              </div>

              </div> 
            </ProductWrapper>
        );
    }
}

Product.propTypes = {
  product: PropTypes.shape({
    id: PropTypes.number,
    img: PropTypes.string,
    title: PropTypes.string,
    price: PropTypes.number,
    inCart: PropTypes.bool,
  }.isRequired
  )

}

//styling
const ProductWrapper = styled.div`
.card{
  border-color: transparent;
  transition:all 1s linear;
}
.card-footer{
  background: transparent;
  border-top: transparent;
  transition: all 1s linear;

}
&hover{
  .card{
    border:0.04rem solid rgba(0,0,0,0.2);
    box-shadow: 2px 2px 5px 0px rgba(0,0,0,0.02);
  }
  .card-footer{
    background: rgba(247,247,247);

  }
}
.img-container{
  position: relative;
  overflow: hidden;

}
.card-img-top{
  transition: all 1s linear;

}
.img-container:hover .card-img-top{
  transform: scale(1.2);

}
.card-btn{
  position: absolute;
  bottom: 0;
  right: 0;
  padding: 0.2rem 0.4rem;
  background: var(--lightBlue);
  border: none;
  color: var(--mainWhite);
  font-size: 1.4rem;
  border-radius: 0.8rem 0 0 0;
  transform: translate(100%,100%);
  transition: all 1s linear;
}
.img-container:hover .card-btn{
  transform: translate(0%,0%);

}
.card-btn: hover{
  color: var(--mainBlue);
  cursor:pointer;


}
`;
