                <Default></Default>
                ): (
                <div>{newarr.length} Results</div>
                )}
            </div>
            <div className="row top">
                <div className="col-1">
                <h3>Department</h3>
                <ul>
                    <li>Categoery 1</li>
                </ul>
                </div>
                <div className="col-3">
                {newarr.length==0 ? (
                    <Default></Default>
                ) : (
                    <>
                        <div className="py-5">
                            <div className="container">
                                    <div className="row"> 
                                    <ProductConsumer>
                                        {

                                            value => {
                                                return (
                                                    value.filteredProducts.map( product => { return <Product key={product.id} product={product}/>;} )
                                                    );
                                            }
                                        }
                                    </ProductConsumer>
                                    </div>
                            </div>
                        </div>
                    </>
                )}
                </div>
            </div>
            </div>
        )
    }
}
