import React, { Component } from 'react';
import { useParams } from 'react-router-dom';
import Default from './Default';
import Product from '../components/Product';
import { ProductConsumer } from '../context';
import { storeProducts } from '../data';


export default class SearchScreen extends Component{
    
    
    

  render(){
    const { name = 'all' } = this.props.match.params;
    const filteredProducts = storeProducts.filter((product) => {
      if (
        
        product.title.toLowerCase().includes(name) ||
        product.company.toLowerCase().includes(name)
      ) {
        return product;
      }
    });
        return (
            <div>
                <div className="col-3">
                {
                    <>
                        <div className="py-5">
                            <div className="container">
                                    <div className="row"> 
                                    {
                                        (filteredProducts.length ==0)?(
                                            <div className="ml-auto">
                                            <div className = "container">
                                                <div className="row">
                                                <div className="col-10 mx-auto text-center text-title text-uppercase pt-5">
                                                <h1 className="display-3">
                                                    404
                                                </h1>
                                                <h1>error</h1>
                                                <h2>page not found</h2>
                                                <h3>the requested URL <span className="text-danger">{this.props.location.pathname}</span> was not found</h3>
                                                </div>
                                                </div>
                                            </div>
                                            </div>
                                        ):
                                        (<ProductConsumer>
                                        {

                                            value => {
                                                return (
                                                    filteredProducts.map( product => { return <Product key={product.id} product={product}/>;} )
                                                    );
                                            }
                                        }
                                    </ProductConsumer>)}
                                    </div>
                            </div>
                        </div>
                    </>
                }
                </div>
            </div>
            
        )
    }
}
